import React, {  useState } from "react";
// import notes from "../notes";
import Note from "./Note";


function MakeNote() {

const Localdata = () => { 
  let datta = JSON.parse(localStorage.getItem('dd'));
console.log(datta);

if(datta) { return datta;}
else { return [];}

}

const [Title,setTitle]=useState(Localdata());
const [Data, setData] = useState({
title : '',
content : '',
});
 
function setT(event){

    const name= event.target.name;
    const value= event.target.value;

   setData(old => {
   if(name==='title'){
    return { title : value, content : old.content}
   }
   else if (name==='content'){
    return { title : old.title, content: value}
   }
    
   })
}
  function add() {
  
         if (Data.title === '' || Data.content === ''){}
         else { 
    setTitle(old=>{
        localStorage.setItem('dd' , JSON.stringify([...old,Data]));
        
        return [...old,Data];
    });

setData({title : '', content : ''});
  }
    // notes.push(Data);
    // console.log(Title);
    // console.log(Data);
    // console.log(notes);
    // console.log(notes);
    // console.log(Title);
  }

    function remove(id) {
      let ld = JSON.parse(localStorage.getItem('dd'));
      // const filtered = ld.filter(item => {
      //   return item.id !== id;
      // })

      ld.splice(id, 1);
      localStorage.setItem('dd' , JSON.stringify(ld));

    }


 return ( <> <div className="inputMain">
    <div className="noteInputMain">
      <div className="h111"> <h1>Make A Note</h1></div>
<input value={Data.title} type="text" id="tittle" name='title' onChange={setT}  required/>
<input value={Data.content} type="text" name='content' onChange={setT}  required/>
<button  onClick={()=>{add()}}>+</button>
    </div>
    </div>
     {/* {arr.map(props => <Note title={props.title}  content={props.content}/>)} */}
  {Title.map((props, index )=> 
  // <Note key={index} title={props.title}  content={props.content}/>
  <div className="note" key={index}>
    
  <h1>{props.title}</h1>
  <p>{props.content}</p>
  <button onClick={()=>remove(index)} className="buttonfinal">-</button>
</div>
    
     )}
     </>)
}

export default MakeNote;
