import React, { useEffect, useState } from "react";
import notes from "../notes";
import Note from "./Note";


function MakeNote() {
var T = []; 
const [Title,setTitle]=useState([]);
const [Data, setData] = useState({
title : '',
content : '',
});




function setT(event){

    const name= event.target.name;
    const value= event.target.value;

   setData(old => {
   if(name==='title'){
    return { title : value, content : old.content}
   }
   else if (name==='content'){
    return { title : old.title, content: value}
   }
    
   })
}
  function add() {
    setTitle(old=>{
        return [...old,Data];
    });

    localStorage.setItem(Data);
    // console.log(Title);
    // console.log(Data);
    // console.log(notes);
    notes.push(Data);
    // console.log(notes);
    // console.log(Title);




    let ff =  localStorage.getItem(Data);
    console.log(ff);
  }


 return ( <> <div className="inputMain">
    <div className="noteInputMain">
      <div className="h111"> <h1>Make A Note</h1></div>
<input type="text" id="tittle" name='title' onChange={setT}  required/>
<input type="text" name='content' onChange={setT}  required/>
<button onClick={()=>{add()}}>+</button>
    </div>
    </div>
    {Title.map(props => <Note title={props.title}  content={props.content}/>)}
     {notes.map(props => <Note key={props.key} title={props.title}  content={props.content}/>)}
     </>)
}

export default MakeNote;
