import React, {  useState } from "react";
// import notes from "../notes";
import Note from "./Note";


function MakeNote() {

const Localdata = JSON.parse(localStorage.getItem('dd'));
console.log(Ldata);
const [Title,setTitle]=useState(Localdata);
const [Data, setData] = useState({
title : '',
content : '',
});
 
function setT(event){

    const name= event.target.name;
    const value= event.target.value;

   setData(old => {
   if(name==='title'){
    return { title : value, content : old.content}
   }
   else if (name==='content'){
    return { title : old.title, content: value}
   }
    
   })
}
  function add() {
    setTitle(old=>{
        return [...old,Data];
    });
    // notes.push(Data);

    localStorage.setItem('dd' , JSON.stringify(Title));
    // console.log(Title);
    // console.log(Data);
    // console.log(notes);
  
    // console.log(notes);
    // console.log(Title);

  }

 return ( <> <div className="inputMain">
    <div className="noteInputMain">
      <div className="h111"> <h1>Make A Note</h1></div>
<input type="text" id="tittle" name='title' onChange={setT}  required/>
<input type="text" name='content' onChange={setT}  required/>
<button onClick={()=>{add()}}>+</button>
    </div>
    </div>
     {/* {arr.map(props => <Note title={props.title}  content={props.content}/>)} */}
     {Title.map(props => <Note key={props.key} title={props.title}  content={props.content}/>)}
     </>)
}

export default MakeNote;
